import { Avatar } from '@material-ui/core';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import React from 'react';
import "./CampaignBox.css";

function CampaignBox({profilePic, username, timestamp, title, target, location, description}) {
    return (
        <div className = "campaignBox">
            <div class = "boxTop">
                <Avatar src = {profilePic} className = "campaignAvatar" />

                <div className = "boxTopInfo">
                    <h3> {username} </h3>
                    <p className = "time"> {timestamp} </p>
                    <p className = "location"> <LocationOnIcon style = {{ color: "red" }} fontSize = "inherit" />{location} </p>
                </div>

                <div className = 'boxBottom'>
                    <h3> {title} </h3>
                    <p> ${target} </p>
                    <p> {description} </p>
                </div>
            </div>
        </div>
    )
}

export default CampaignBox
