export const initialState = {
    user: null,
    link: null,
};

export const actionTypes = {
    SET_USER: "SET_USER",
    SET_LINK: "SET_LINK",
};

const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case actionTypes.SET_USER:
            return {
                ...state, 
                user: action.user,
            };
        case actionTypes.SET_LINK:
            return {
                ...state,
                link: action.link,
            }

            default:
                return state;
    }
};

export default reducer;
