import React, { useState } from 'react';
import "./CampaignForm.css";
import { Button } from '@material-ui/core';
import SupervisedUserCircleIcon from '@material-ui/icons/SupervisedUserCircle';
import { useStateValue } from "./StateProvider";
import db from "./firebase";
import firebase from "firebase";



function CampaignForm() {

    const [{ user }, dispatch] = useStateValue();
    const [input, setInput] = useState("");

    const handleSubmit = e => {
        e.preventDefault();

        db.collection("campaigns").add({
            message: input,
            timestamp: firebase.firestore.FieldValue.serverTimestamp(),
            target: input,
            username: user.displayName, 
            location: input,
            description: input,
        });

        setInput("");
    }

    return (
        <div className = "campaign">

            <div className = "campaignFormLeft">
                <SupervisedUserCircleIcon 
                    fontSize = "large"
                    style = {{fontSize: 400}}
                />
                <h3>Make change happen!</h3> 
                <p>Create your campaign with Credo now</p>
            </div>
            <div className = "campaignFormRight">
                <form>
                    <input
                        type = "text"
                        name =  "title"
                        value = {input}
                        onChange = {e => setInput(e.target.value)}
                        className = "formTitle"
                        placeholder = {"Title of Your Campaign"}
                        // onChange = {e => setInput(e.target.value)}
                        />
                    <input
                        type ="location"
                        name = "location"
                        value = {input}
                        onChange = {e => setInput(e.target.value)}
                        className = "formLocation"
                        placeholder = {"Campaign Location"}
                        // onChange = {e => setInput(e.target.value)}
                    />
                    <input
                        type = "number"
                        name = "target donation"
                        value = {input}
                        onChange = {e => setInput(e.target.value)}
                        className = "formCampaignTarget"
                        placeholder = {"Your Donation Target in US Dollars"}
                        // onChange = {e => setInput(e.target.value)}
                    />
                    <input
                        type = "text"
                        name = "description"
                        value = {input}
                        onChange = {e => setInput(e.target.value)}
                        id = "textarea"
                        className = "formDescription"
                        placeholder = {"Campaign Description"}
                    />
                    </form>
                    <Button onClick = {handleSubmit} type = "submit">
                        Start Campaign
                    </Button>
            </div>
        </div>
    )
}

export default CampaignForm;
