export const initialState = {
    user: null,
    link: '/login',
};

export const actionTypes = {
    SET_USER: "SET_USER",
    SET_LINK: "SET_LINK",
};

const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
        case actionTypes.SET_USER:
            return {
                ...state, 
                user: action.user,
            };

            default:
                return state;
    }
};

export default reducer;
