import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import CampaignBox from './CampaignBox';
import { useStateValue } from './StateProvider';
import { Button } from '@material-ui/core';
import "./CampaignsFeed.css";

function CampaignsFeed() {

    const history = useHistory();

    const handleSubmitNewCampaign = e => {
        e.preventDefault();
        history.push("/create-campaign");
    }

    const [{user} , dispatch] = useStateValue();

    const [otherCamps, setOtherCamps] = useState([]);

    useEffect(() => { 
        db.collection("campaigns-full").where("uid", "!=", user.uid).onSnapshot(snapshot => {
          const lists = snapshot.docs.map(doc => ({
            id: doc.id,
            data: doc.data(),
          }))
          setOtherCamps(lists)
        })
    }, []);

    return (
        <div className = "campaignsFeed"> 
            <h1>Campaigns Near You </h1>
            <div className = "campaignBox">
                <CampaignBox
                    title = "Black Lives Matter"
                    description = "Breonna Taylor"
                    username = "arda"
                    target = "1000"
                    location = 'Oakland'
                    timestamp = {new Date().toUTCString()}
                    image = "https://static.scientificamerican.com/sciam/cache/file/1A02A175-8CA4-41E1-A596DB34E17789C8_source.jpg?w=590&h=800&403DAC09-B41C-4C73-857D14754B07000D"
                />
            </div>
        </div>
    )
}

export default CampaignsFeed
