import MainPage from "./MainPage";
import CampaignPage from "./CampaignPage";

function App() {
  const [{ user }, dispatch] = useStateValue();

  return (
    <div className="app">
      if (!user) {
        <Login />
      } else {
        <MainPage />
      }


    </div> 
  );
}

export default App;
