import React, { useState } from "react";
import MessageSender from './MessageSender.js';
import Post from "./Post.js";
import db from "./firebase";

function Feed() {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
        db.collection('posts').onSnapshot((snapshot) =>  
            setPosts(snapshot.docs.map((doc) => ({ id: doc.id, data: doc.data() })))
        );
    }, []);
    return <div className = "feed">
        <MessageSender />

        <Post 
            profilePic = "http://humanorigins.si.edu/sites/default/files/styles/grid_thumbnail/public/images/landscape/floresiensis_JG_Recon_Head_CC_3qtr_lt_l1.jpg?h=34bbd072&itok=nM9yxAfJ"
            message = "WOW this works"
            timestamp = "12.45"
            username = "Human"
            image = "https://images.theconversation.com/files/184420/original/file-20170903-27238-89w6fa.jpg?ixlib=rb-1.1.0&q=45&auto=format&w=926&fit=clip"
        />
        <Post
           profilePic = "https://media-exp1.licdn.com/dms/image/C5603AQGHdWDB2rpS8w/profile-displayphoto-shrink_200_200/0/1614102663223?e=1623888000&v=beta&t=TCHpEwOXPOHfC7-nAqpr8TbSA6vEda8entjX4cJfvqM" 
           message = "I am too fat :("
           username = "Bitchie"
            />
        <Post />
    </div>
}

export default Feed;
